DROP TABLE IF EXISTS radio_matches;
CREATE TABLE radio_matches (
    match_id INT AUTO_INCREMENT PRIMARY KEY,

    ls10_ra DOUBLE,
    ls10_dec DOUBLE,

    -- VLASS
    vlass_name VARCHAR(255),
    vlass_ra DOUBLE,
    vlass_dec DOUBLE,
    vlass_flux DOUBLE,

    -- RACS
    racs_name VARCHAR(255),
    racs_ra DOUBLE,
    racs_dec DOUBLE,
    racs_flux DOUBLE,

    FOREIGN KEY (ls10_ra, ls10_dec)
        REFERENCES optical_nway_counterparts(ls10_ra, ls10_dec)
);


drop table if exists simbad_matches;
CREATE TABLE simbad_matches (
	-- simbad_id INT AUTO_INCREMENT PRIMARY KEY, 
    ls10_ra DOUBLE,
    ls10_dec DOUBLE,
    simbad_name VARCHAR(255),
    simbad_ra DOUBLE,
